mapper.select:
SELECT *
FROM product p
INNER JOIN cate c ON p.cate_id = c.cate_id


        AND p.product_name LIKE CONCAT('%', #{productName}, '%')


        AND p.product_price  >=  #{minPrice}


        AND p.product_price  <=  #{maxPrice}




          and c.cate_id  =  #{cateId}

SELECT * FROM product p
                  INNER JOIN cate c ON p.cate_id=c.cate_id


        AND p.product_name LIKE CONCAT('%', #{productName}, '%')


        AND p.product_price  >=  #{minPrice}


        AND p.product_price  <=  #{maxPrice}




          and c.cate_id  =  #{cateId}
